import * as d3 from 'd3-selection';
import * as d3Axis from 'd3-axis';
import * as d3Scale from 'd3-scale';
import { Axis } from 'd3-axis';

@Component({
    selector: 'page-axes',
    templateUrl: 'axes.html',
})

export class AxesPage {

    scalePage = ScalesPage;

    xAxis:Axis<any>;

    constructor( public navCtrl: NavController, private elementRef:ElementRef ) {  }

    ionViewDidLoad() {

        this.createAxis();

        this.createSVG();

        this.createOtherAxis();

    }

    createAxis(): void {

        //Create the Scale we will use for the Axis
        let axisScale = d3Scale
            .scaleLinear()
            .domain( [ 0, 100 ] )
            .range( [ 0, 300 ] );

        // Generate the D3.js Axis
        this.xAxis =  d3Axis.axisBottom( axisScale );

    }


    createSVG(): void {
        //Create the SVG Viewport selection
        let svgContainer = d3.select( '#axis-svg' ).append( 'svg' )
            .attr( 'width', 300 )
            .attr( 'height', 100 );

        // Create a group Element for the Axis elements and call the xAxis function
        svgContainer.append('g')
            .call( this.xAxis );
    }


    createOtherAxis(): void {

        this.elementRef.nativeElement.querySelectorAll( '.svg-axis' ).forEach( container => {

            let config = JSON.parse( container.getAttribute( 'data-config' ) );

            //Create the SVG Viewport selection
            let svgContainer = d3.select( '#' + container.id ).append( 'svg' )
                .attr( 'width', config.width )
                .attr( 'height', config.height );

            //Create the Scale we will use for the Axis
            let axisScale = d3Scale
                .scaleLinear()
                .domain( config.domain )
                .range( config.range );

            // Create a group Element for the Axis elements and
            let axisGroup = svgContainer.append( 'g' );

            // Generate the D3.js Axis
            let axis:Axis<any>;
            let translateValue = [ 0, 0 ];
            switch ( config.axisPosition ) {
                case 'top':
                    axis =  d3Axis.axisTop( axisScale );
                    translateValue = [ 0, 20 ];
                    break;
                case 'left':
                    axis =  d3Axis.axisLeft( axisScale );
                    translateValue = [ 20, 0 ];
                    break;
                case 'right':
                    axis =  d3Axis.axisRight( axisScale );
                    break;
                default:
                    axis =  d3Axis.axisBottom( axisScale );
                    break;
            }


            // Call the xAxis function on the axis group
            axisGroup.call( axis );

            // Call the translate to display the axis in the viewport
            axisGroup.attr( 'transform', 'translate(' + translateValue.join() + ')' );

        });

    }
}

import { Component } from '@angular/core';

import * as d3 from 'd3-selection';

@Component({
    selector: 'page-basic-elements',
    templateUrl: 'basic-elements.html',
})
export class BasicElementsPage {

    constructor() {

    }

    ionViewDidLoad():void {
        this.createCircles();
        this.createCoordinateCircles();

    }

    createCircles():void {

        // the data set
        let circleRadii = [ 40, 20, 10 ];

        // an SVG element to hold the SVG Circles
        let svgContainer = d3.select( '#circles-basic' ).append( 'svg' )
            .attr( 'width', 300 )
            .attr( 'height', 100 )
            .style( 'border', '1px solid black' );

        let circles = svgContainer
            // selectAll circle
            .selectAll( 'circle' )
            // bind the data
            .data( circleRadii )
            // select the virtual .enter() selection
            .enter()
            // append the SVG circle elements
            .append( 'circle' );

        // Specify the attributes for each circle.
        circles
            // cx - The x-axis coordinate of the center of the circle
            .attr( 'cx', 50 )
            // cy - The y-axis coordinate of the center of the circle
            .attr( 'cy', 50 )
            // r - The radius of the circle.
            .attr( 'r',  d => d )
            // use D3.js' .style() operator to modify the CSS style property
            .style( 'fill', d => {
                let returnColor;
                if ( d === 40 ) {
                    returnColor = 'green';
                }
                else if ( d === 20 ) { returnColor = 'purple'; }
                else if ( d === 10 ) { returnColor = 'red'; }
                return returnColor;
            });

    }

    createCoordinateCircles():void {

        let spaceCircles = [ 30, 70, 110 ];

        let svgContainer = d3.select( '#coordinateCircles' ).append( 'svg' )
            .attr( 'width', 300 )
            .attr( 'height', 200 )
            .style( 'border', '1px solid black' );

        let circles = svgContainer.selectAll( 'circle' )
            .data( spaceCircles )
            .enter()
            .append( 'circle' );

        circles
            // add the attributes to the circle selection.
            // The data set specifies the circle center's x-coordinate and y-coordinate,
            // use D3.js to update the "cx" and "cy" of the specified data above
            .attr( 'cx', d => d )
            .attr( 'cy', d => d )
            .attr( 'r', 20 )
            .style( 'fill', d => {
                let returnColor;
                if ( d === 30 ) {
                    returnColor = 'green';
                }
                else if ( d === 70 )  { returnColor = 'purple'; }
                else if ( d === 110 ) { returnColor = 'red'; }
                return returnColor;
            });

    }
}

